-- Functions

-- Cooldown
local function Cool_Down()
	while Cooldown > 0 do
		wait(.5)
		Cooldown -= 1
	end
end


-- Sounds
local function UIClickSound()
	if not UISound.IsPlaying then
		UISound:Play()
	end
end

local function StartSound()
	if not Startting.IsPlaying then
		Startting:Play()
	end
end

local function BeepSound()
	if not Beep.IsPlaying then
		Beep:Play()
	end
end


-- Tests
local function prints()
	print(GameCountdown)
end

