	if Diff == "easy" then
		if Pelajaran == "MTKWajib" or Pelajaran == "Fisika" or Pelajaran == "Kimia" or Pelajaran == "MTKPeminatan" then
			GameTime = NumbersEasy
		else
			GameTime = EasyTime
		end

		Amount = 5
	elseif Diff == "medium" then
		if Pelajaran == "MTKWajib" or Pelajaran == "Fisika" or Pelajaran == "Kimia" or Pelajaran == "MTKPeminatan" then
			GameTime = NumbersMedium
