			GameTime = NumbersHard
		else
			GameTime = HardTime
		end
		Amount = 20
	end
	
	running = true
	
	BackButton.Visible = false
	Title.Visible = false
	
	-- Countdown
	BeepSound()
	while GameCountdown > 0 and running == true do
		
		BeepSound()
		CountdownText.Text = GameCountdown
		GameCountdown -= 1
		wait(1)
		
		if GameCountdown == 0 then
			
